#Gera uma tabela contendo o nome da coluna, o tipo e o tamanho de caracteres
x <- data.frame(type=sapply(inpc, class),
                nc=unlist(lapply(inpc, function(x) max(nchar(x), na.rm = T))),
                tm=as.numeric(unlist(lapply(inpc, function(x) max(x, na.rm = T))))) %>%
  # Ajustar os casos em que todas as colunas são vazias
  dplyr::mutate(nc=ifelse(nc==-Inf,2,nc),
                # Determinar o tamanho dos campos VARCHAR e fazer o de para dos demais campos
                coltype=case_when(type=="character"~paste0("VARCHAR(",nc,")"),
                                  type=="numeric"~'float',
                                  type %in% c("integer","integer64")~"bigint",
                                  TRUE~'VARCHAR(1)'),
                coltype=case_when(type=="integer" & tm >= -2^31 & tm <= 2^31-1~"int",
                                  TRUE~coltype),
                coltype=case_when(type=="integer" & tm >= -2^15 & tm <= 2^15-1~"smallint",
                                  TRUE~coltype),
                coltype=case_when(type=="integer" & tm >= 0 & tm <= 255~"int",
                                  TRUE~coltype))

columnTypes <- setNames(as.list(x$coltype),names(inpc))

